const menuTrigger=document.querySelector(".nav-burger-container"),burgerLine=document.querySelectorAll(".burger-line"),menu=document.querySelector(".menu-container"),header=document.querySelector("header");menuTrigger.addEventListener("click",function(e){e.preventDefault(),menu.classList.toggle("show"),burgerLine.forEach(e=>{e.classList.toggle("active")})}),window.addEventListener("scroll",function(){const e=window.pageYOffset;e>50?(header.classList.add("show"),menuTrigger.classList.add("show")):e<50&&(header.classList.remove("show"),menuTrigger.classList.remove("show"))});